sql-item.sql:
select u.user_id,u.`name`,bcr.mobile,w.openid,u.register_time, case when wi.user_name is null then '燕燕' else wi.user_name end consultant_name, case when wi.wx_code is null then 'yuncaigou01' else wi.wx_code end consultant_wechat ,dev.dev_count from (
select user_id, name ,user_biz_type, case when register_time<='2016-06-06 00:00:00' then '2016-06-06 00:00:00' else register_time end register_time From b2b_user.uuc_user where user_biz_type=2
) u left join b2b_user.uuc_user_identity uui on u.user_id=uui.user_id left join b2b_user_business.business_card_record bcr on u.user_id=bcr.user_id left JOIN b2b_company.uuc_developer d on bcr.company_id=d.developer_id inner join b2b_user.uuc_company_contract con on d.developer_id=con.company_id and con.sign_status=1 left join dotnet_operation.op_connect_wxinfo wi ON FIND_IN_SET(con.contract_area,wi.respon_areas) AND wi.business_type=1 left join ( SELECT w.userid,w.openid FROM m_yct.m_wx2ycguser w inner join m_yct.zq_user u on w.openid=u.openid WHERE w.wxappid=@appid group by w.userid ) w on u.user_id=w.userid cross JOIN ( select ceiling(count(developer_id)/100)*100 as dev_count From b2b_company.uuc_developer ) dev where uui.user_id is null and datediff(now(),u.register_time)=8
